function publishKeyValuePairs(obj, currentTopic) {
    // Publish the entire object/array at the current level
    let baseMessage = {
        payload: obj,
        topic: currentTopic
    };
    node.send(baseMessage);

    // Iterate through the properties of the object
    for (let key in obj) {
        if (obj.hasOwnProperty(key)) {
            let newTopic = currentTopic + "/" + key;
            if (typeof obj[key] === 'object' && obj[key] !== null) {
                if (Array.isArray(obj[key])) {
                    // Publish the entire array before drilling down
                    let arrayMessage = {
                        payload: obj[key],
                        topic: newTopic
                    };
                    node.send(arrayMessage);

                    // Iterate through the elements of the array
                    obj[key].forEach((item, index) => {
                        if (typeof item === 'object' && item !== null) {
                            // If an element of the array is an object, recurse
                            publishKeyValuePairs(item, newTopic + "/" + index);
                        } else {
                            // If an element of the array is a primitive, send it
                            let message = {
                                payload: item,
                                topic: newTopic + "/" + index
                            };
                            node.send(message);
                        }
                    });
                } else {
                    // If the value is a non-array object, recurse
                    publishKeyValuePairs(obj[key], newTopic);
                }
            } else {
                // If the value is a primitive, send it
                let message = {
                    payload: obj[key],
                    topic: newTopic
                };
                node.send(message);
            }
        }
    }
}

